pragma solidity 0.8.25;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IStrategy} from "../interfaces/vesper/IStrategy.sol";
import {IVesperPool} from "../interfaces/vesper/IVesperPool.sol";
import {ISwapper} from "../interfaces/swapper/ISwapper.sol";

// solhint-disable no-empty-blocks
abstract contract Strategy is Initializable, UUPSUpgradeable, IStrategy {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    error AddressIsNull();
    error CanNotSweepToken();
    error FeeCollectorNotSet();
    error InvalidStrategy();
    error NotEnoughAmountOut();
    error Unauthorized();

    event UpdatedFeeCollector(address oldFeeCollector, address newFeeCollector);
    event UpdatedSwapper(ISwapper oldSwapper, ISwapper newSwapper);

    address internal constant ETH = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    uint256 internal constant MAX_UINT_VALUE = type(uint256).max;
    /// @custom:storage-location erc7201:vesper.storage.Strategy
    struct StrategyStorage {
        IERC20 _collateralToken;
        address _pool;
        address _receiptToken;
        address _feeCollector;
        ISwapper _swapper;
        EnumerableSet.AddressSet _keepers;
        string _name;
    }

    bytes32 private constant StrategyStorageLocation =
        keccak256(abi.encode(uint256(keccak256("vesper.storage.Strategy")) - 1)) & ~bytes32(uint256(0xff));

    function _getStrategyStorage() private pure returns (StrategyStorage storage $) {
        bytes32 _location = StrategyStorageLocation;
        assembly {
            $.slot := _location
        }
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function __Strategy_init(
        address pool_,
        address swapper_,
        address receiptToken_,
        string memory name_
    ) internal onlyInitializing {
        __UUPSUpgradeable_init();
        if (pool_ == address(0) || swapper_ == address(0) || receiptToken_ == address(0)) revert AddressIsNull();
        StrategyStorage storage $ = _getStrategyStorage();
        $._pool = pool_;
        $._collateralToken = IVesperPool(pool_).token();
        $._receiptToken = receiptToken_;
        // Set pool governor as default feeCollector
        $._feeCollector = IVesperPool(pool_).governor();
        $._swapper = ISwapper(swapper_);
        $._name = name_;
        $._keepers.add(msg.sender);
    }

    modifier onlyGovernor() {
        if (msg.sender != governor()) revert Unauthorized();
        _;
    }

    modifier onlyKeeper() {
        if (!_getStrategyStorage()._keepers.contains(msg.sender)) revert Unauthorized();
        _;
    }

    modifier onlyPool() {
        if (msg.sender != pool()) revert Unauthorized();
        _;
    }

    function collateralToken() public view override returns (IERC20) {
        return _getStrategyStorage()._collateralToken;
    }

    function feeCollector() public view returns (address) {
        return _getStrategyStorage()._feeCollector;
    }

    function governor() public view returns (address) {
        return IVesperPool(pool()).governor();
    }

    function isActive() external view override returns (bool) {
        (bool _isActive, , , , , , , , ) = IVesperPool(pool()).strategy(address(this));
        return _isActive;
    }

    /// @notice Check whether given token is reserved or not. Reserved tokens are not allowed to sweep.
    function isReservedToken(address token_) public view virtual override returns (bool);

    /// @notice Return list of keepers
    function keepers() external view override returns (address[] memory) {
        return _getStrategyStorage()._keepers.values();
    }

    function NAME() external view returns (string memory) {
        return _getStrategyStorage()._name;
    }

    function pool() public view override returns (address) {
        return _getStrategyStorage()._pool;
    }

    function poolAccountant() external view returns (address) {
        return IVesperPool(pool()).poolAccountant();
    }

    function swapper() public view returns (ISwapper) {
        return _getStrategyStorage()._swapper;
    }

    function receiptToken() public view virtual override returns (address) {
        return _getStrategyStorage()._receiptToken;
    }

    /// @notice Returns total collateral locked in the strategy
    function tvl() external view virtual returns (uint256);

    function VERSION() external pure virtual override returns (string memory) {
        return "5.5.0";
    }

    /**
     * @notice onlyGovernor: Add given address in keepers list.
     * @param keeperAddress_ keeper address to add.
     */
    function addKeeper(address keeperAddress_) external onlyGovernor {
        _getStrategyStorage()._keepers.add(keeperAddress_);
    }

    /// @dev OnlyKeeper: Approve all required tokens
    function approveToken(uint256 approvalAmount_) external onlyKeeper {
        _approveToken(approvalAmount_);
    }

    /// @notice OnlyKeeper: Claim rewards from protocol.
    /// @dev This function will only be used when protocol doesn't offer claim by anyone.
    function claimRewards() external onlyKeeper {
        _claimRewards();
    }

    /**
     * @notice OnlyKeeper: Rebalance profit, loss and investment of this strategy.
     *  Calculate profit, loss and payback of this strategy and realize profit/loss and
     *  withdraw fund for payback, if any, and submit this report to pool.
     * @return _profit Realized profit in collateral.
     * @return _loss Realized loss, if any, in collateral.
     * @return _payback If strategy has any excess debt, we have to liquidate asset to payback excess debt.
     */
    function rebalance() external onlyKeeper returns (uint256 _profit, uint256 _loss, uint256 _payback) {
        return _rebalance();
    }

    /**
     * @notice onlyGovernor: Remove given address from keepers list.
     * @param keeperAddress_ keeper address to remove.
     */
    function removeKeeper(address keeperAddress_) external onlyGovernor {
        _getStrategyStorage()._keepers.remove(keeperAddress_);
    }

    /// @notice onlyKeeper:: Swap given token into collateral token.
    function swapToCollateral(IERC20 tokenIn_, uint256 minAmountOut_) external onlyKeeper returns (uint256 _amountOut) {
        StrategyStorage storage $ = _getStrategyStorage();
        IERC20 _collateralToken = $._collateralToken;
        address _swapper = address($._swapper);

        if (address(tokenIn_) == address(_collateralToken) || isReservedToken(address(tokenIn_)))
            revert CanNotSweepToken();
        uint256 _collateralBefore = _collateralToken.balanceOf(address(this));
        uint256 _amountIn = tokenIn_.balanceOf(address(this));
        if (_amountIn > 0) {
            if (_amountIn > tokenIn_.allowance(address(this), _swapper)) {
                // if needed, forceApprove will set approval to zero before setting new value.
                tokenIn_.forceApprove(_swapper, MAX_UINT_VALUE);
            }
            _swapExactInput(address(tokenIn_), address(_collateralToken), _amountIn);
        }
        _amountOut = _collateralToken.balanceOf(address(this)) - _collateralBefore;
        if (_amountOut < minAmountOut_) revert NotEnoughAmountOut();
    }

    /**
     * @notice onlyKeeper: sweep given token to feeCollector of strategy
     * @param fromToken_ token address to sweep
     */
    function sweep(address fromToken_) external override onlyKeeper {
        address _feeCollector = feeCollector();
        if (_feeCollector == address(0)) revert FeeCollectorNotSet();
        if (fromToken_ == address(collateralToken()) || isReservedToken(fromToken_)) revert CanNotSweepToken();
        if (fromToken_ == ETH) {
            Address.sendValue(payable(_feeCollector), address(this).balance);
        } else {
            uint256 _amount = IERC20(fromToken_).balanceOf(address(this));
            IERC20(fromToken_).safeTransfer(_feeCollector, _amount);
        }
    }

    /**
     * @notice onlyGovernor: Update fee collector
     * @param feeCollector_ fee collector address
     */
    function updateFeeCollector(address feeCollector_) external onlyGovernor {
        if (feeCollector_ == address(0)) revert AddressIsNull();
        StrategyStorage storage $ = _getStrategyStorage();
        emit UpdatedFeeCollector($._feeCollector, feeCollector_);
        $._feeCollector = feeCollector_;
    }

    /**
     * @notice onlyGovernor: Update swapper
     * @param swapper_ swapper address
     */
    function updateSwapper(ISwapper swapper_) external onlyGovernor {
        if (address(swapper_) == address(0)) revert AddressIsNull();
        StrategyStorage storage $ = _getStrategyStorage();
        emit UpdatedSwapper($._swapper, swapper_);
        $._swapper = swapper_;
    }

    /**
     * @notice onlyPool: Withdraw collateral token from end protocol.
     * @param amount_ Amount of collateral token
     */
    function withdraw(uint256 amount_) external override onlyPool {
        StrategyStorage storage $ = _getStrategyStorage();
        IERC20 _collateralToken = $._collateralToken;
        address _pool = $._pool;
        uint256 _collateralHere = _collateralToken.balanceOf(address(this));
        if (_collateralHere >= amount_) {
            _collateralToken.safeTransfer(_pool, amount_);
        } else {
            _withdrawHere(amount_ - _collateralHere);
            // Do not assume _withdrawHere() will withdraw exact amount. Check balance again and transfer to pool
            _collateralHere = _collateralToken.balanceOf(address(this));
            _collateralToken.safeTransfer(_pool, Math.min(amount_, _collateralHere));
        }
    }

    function _approveToken(uint256 amount_) internal virtual {
        StrategyStorage storage $ = _getStrategyStorage();
        $._collateralToken.forceApprove($._pool, amount_);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyGovernor {}

    function _claimRewards() internal virtual {}

    function _rebalance() internal virtual returns (uint256 _profit, uint256 _loss, uint256 _payback);

    function _swapExactInput(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    ) internal returns (uint256 _amountOut) {
        _amountOut = swapper().swapExactInput(tokenIn_, tokenOut_, amountIn_, 1, address(this));
    }

    function _trySwapExactInput(address tokenIn_, address tokenOut_, uint256 amountIn_) internal returns (uint256) {
        try swapper().swapExactInput(tokenIn_, tokenOut_, amountIn_, 1, address(this)) returns (uint256 _amountOut) {
            return _amountOut;
        } catch {
            return 0;
        }
    }

    // These methods must be implemented by the inheriting strategy
    function _withdrawHere(uint256 amount_) internal virtual;
}
","keccak256":"0x99fba93ad82031b384caabef2dceaa7ac9fd17cb52e905fb8ffd23eeab972c09","license":"MIT"},"contracts/strategies/convex/Convex.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.25;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ILiquidityGaugeV2} from "../../interfaces/curve/ILiquidityGauge.sol";
import {IConvex, IRewards} from "../../interfaces/convex/IConvexForCurve.sol";
import {CurveBase} from "../curve/CurveBase.sol";

// Convex Strategy
contract Convex is CurveBase {
    using SafeERC20 for IERC20;

    error BoosterDepositFailed();
    error IncorrectLpToken();
    error RewardClaimFailed();
    error UnstakeFromConvexFailed();

    /// @custom:storage-location erc7201:vesper.storage.Strategy.Convex
    struct ConvexStorage {
        address _convexToken;
        IConvex _booster;
        IRewards _convexRewards;
        uint256 _convexPoolId;
    }

    bytes32 private constant ConvexStorageLocation =
        keccak256(abi.encode(uint256(keccak256("vesper.storage.Strategy.Convex")) - 1)) & ~bytes32(uint256(0xff));

    function _getConvexStorage() internal pure returns (ConvexStorage storage $) {
        bytes32 _location = ConvexStorageLocation;
        assembly {
            $.slot := _location
        }
    }

    function initialize(
        CurveBase.CurveInitParams memory params_,
        IConvex booster_,
        uint256 convexPoolId_
    ) external initializer {
        __CurveBase_init(params_);

        if (address(booster_) == address(0)) revert AddressIsNull();

        (address _lp, , , address _rewards, , ) = booster_.poolInfo(convexPoolId_);
        if (_lp != address(receiptToken())) revert IncorrectLpToken();

        ConvexStorage storage $ = _getConvexStorage();
        $._booster = booster_;
        $._convexRewards = IRewards(_rewards);
        $._convexPoolId = convexPoolId_;
    }

    function booster() public view returns (IConvex) {
        return _getConvexStorage()._booster;
    }

    function convexPoolId() public view returns (uint256) {
        return _getConvexStorage()._convexPoolId;
    }

    function convexRewards() public view returns (IRewards) {
        return _getConvexStorage()._convexRewards;
    }

    function convexToken() public view returns (address) {
        return _getConvexStorage()._convexToken;
    }

    function lpBalanceStaked() public view override returns (uint256 _total) {
        _total = convexRewards().balanceOf(address(this));
    }

    function _approveToken(uint256 amount_) internal override {
        super._approveToken(amount_);
        curveLp().forceApprove(address(booster()), amount_);
    }

    function _deposit() internal override {
        // 1. deposit collateral in Curve and stake LP in Curve Gauge.
        super._deposit();

        // 2. Unstake LP from Curve Gauge
        _unstakeLpFromCurve();

        // 3. Stake LP in Convex Booster
        uint256 _balance = curveLp().balanceOf(address(this));
        if (_balance > 0) {
            if (!booster().deposit(convexPoolId(), _balance, true)) revert BoosterDepositFailed();
        }
    }

    function _unstakeLpFromCurve() internal {
        ILiquidityGaugeV2 _curveGauge = curveGauge();
        uint256 _lpStakedInCurve = _curveGauge.balanceOf(address(this));
        if (_lpStakedInCurve > 0) {
            _curveGauge.withdraw(_lpStakedInCurve);
        }
    }

    /// @dev Don't claiming rewards because `_claimRewards()` already does that
    function _unstakeLp(uint256 _amount) internal override {
        if (_amount > 0) {
            if (!convexRewards().withdrawAndUnwrap(_amount, false)) revert UnstakeFromConvexFailed();
        }
    }
}
","keccak256":"0x9426b26e86b8a57175b9012c86423f04057e884f39f9c09468c23d794e8371be","license":"MIT"},"contracts/strategies/curve/CurveBase.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.25;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {Strategy} from "../Strategy.sol";
import {IVesperPool} from "../../interfaces/vesper/IVesperPool.sol";
import {IWETH} from "../../interfaces/IWETH.sol";
import {IMasterOracle} from "../../interfaces/one-oracle/IMasterOracle.sol";
import {IAddressProvider} from "../../interfaces/curve/IAddressProvider.sol";
import {IDepositAndStake, IWithdraw} from "../../interfaces/curve/ICurve.sol";
import {ILiquidityGaugeV2} from "../../interfaces/curve/ILiquidityGauge.sol";
import {ILiquidityGaugeFactory} from "../../interfaces/curve/ILiquidityGaugeFactory.sol";
import {IMetaRegistry} from "../../interfaces/curve/IMetaRegistry.sol";
import {ITokenMinter} from "../../interfaces/curve/ITokenMinter.sol";

/// @title Base contract for Curve-related strategies
abstract contract CurveBase is Strategy {
    using SafeERC20 for IERC20;

    error CurveGaugeIsNull();
    error CurveLpIsNull();
    error CurvePoolZapIsNull();
    error InvalidCollateral();
    error InvalidSlippage();
    error NotAllowedToSendEther();
    error OnlyOneEthAllowedInUnderlying();
    error SlippageTooHigh();

    event SlippageUpdated(uint256 oldSlippage, uint256 newSlippage);
    event MasterOracleUpdated(IMasterOracle oldMasterOracle, IMasterOracle newMasterOracle);
    event WithdrawOneCoinToggled(bool newValue);

    ITokenMinter public constant CRV_MINTER = ITokenMinter(0xd061D61a4d941c39E5453435B6345Dc261C2fcE0); // This contract only exists on mainnet
    ILiquidityGaugeFactory public constant GAUGE_FACTORY =
        ILiquidityGaugeFactory(0xabC000d88f23Bb45525E447528DBF656A9D55bf5); // Act as CRV_MINTER on side chains
    IAddressProvider public constant ADDRESS_PROVIDER = IAddressProvider(0x5ffe7FB82894076ECB99A30D6A32e969e6e35E98); // Same address to all chains
    uint256 private constant META_REGISTRY_ADDRESS_ID = 7;
    uint256 private constant MAX_BPS = 10_000;

    // Initialize params. Using this struct to mitigate stack too deep error.
    // It is being used during initialize only.
    struct CurveInitParams {
        address pool;
        address swapper;
        address curvePool;
        address curvePoolZap;
        address depositAndStake;
        bool useDynamicArray;
        uint256 slippage;
        address weth;
        address masterOracle;
        string name;
    }

    /// @custom:storage-location erc7201:vesper.storage.Strategy.CurveBase
    struct CurveBaseStorage {
        address _curvePool;
        IERC20 _curveLp;
        ILiquidityGaugeV2 _curveGauge;
        address _curvePoolZap;
        address _depositAndStake;
        IWETH _weth;
        IMasterOracle _masterOracle;
        address _depositContract;
        address _curvePoolForDeposit;
        uint256 _slippage;
        uint256 _collateralIdx;
        address[] _underlyingTokens;
        bool _isMetaPool;
        bool _useDynamicArray;
        bool _useUnderlying;
        bool _withdrawOneCoin;
    }

    bytes32 private constant CurveBaseStorageLocation =
        keccak256(abi.encode(uint256(keccak256("vesper.storage.Strategy.CurveBase")) - 1)) & ~bytes32(uint256(0xff));

    function _getCurveBaseStorage() internal pure returns (CurveBaseStorage storage $) {
        bytes32 _location = CurveBaseStorageLocation;
        assembly {
            $.slot := _location
        }
    }

    function __CurveBase_init(CurveInitParams memory params_) internal initializer {
        // init require non-zero value for receiptToken hence setting it to 0x1.
        // receiptToken is overridden in this contract to return curveLp address as receiptToken
        __Strategy_init(params_.pool, params_.swapper, address(0x1), params_.name);

        if (
            params_.curvePool == address(0) ||
            params_.depositAndStake == address(0) ||
            params_.weth == address(0) ||
            params_.masterOracle == address(0)
        ) revert AddressIsNull();

        IMetaRegistry _registry = IMetaRegistry(ADDRESS_PROVIDER.get_address(META_REGISTRY_ADDRESS_ID));

        address _curveLp = _registry.get_lp_token(params_.curvePool);
        if (_curveLp == address(0)) revert CurveLpIsNull();

        address _curveGauge = _registry.get_gauge(params_.curvePool);
        if (_curveGauge == address(0)) revert CurveGaugeIsNull();

        CurveBaseStorage storage $ = _getCurveBaseStorage();
        bool _isLendingTokenPool;
        ($._underlyingTokens, $._collateralIdx, $._isMetaPool, _isLendingTokenPool) = _getCurvePoolInfo(
            _registry,
            params_.curvePool,
            params_.weth
        );
        $._curvePool = params_.curvePool;
        $._curveLp = IERC20(_curveLp);
        $._curveGauge = ILiquidityGaugeV2(_curveGauge);
        $._curvePoolZap = params_.curvePoolZap;
        $._depositAndStake = params_.depositAndStake;
        $._weth = IWETH(params_.weth);
        $._masterOracle = IMasterOracle(params_.masterOracle);
        $._slippage = params_.slippage;
        $._useDynamicArray = params_.useDynamicArray;

        if ($._isMetaPool) {
            if (params_.curvePoolZap == address(0)) revert CurvePoolZapIsNull();
            $._depositContract = params_.curvePoolZap;
            $._curvePoolForDeposit = params_.curvePool;
        } else {
            $._depositContract = params_.curvePool;
        }

        if (_isLendingTokenPool && params_.curvePoolZap != address(0)) {
            $._depositContract = params_.curvePoolZap;
            // useUnderlying is false in this case and false is default to no need to set.
        } else {
            // otherwise set useUnderlying
            $._useUnderlying = _isLendingTokenPool;
        }
    }

    receive() external payable {
        CurveBaseStorage memory s = _getCurveBaseStorage();
        address _curvePool = s._curvePool;
        IWETH _weth = s._weth;
        if (msg.sender != _curvePool && msg.sender != address(_weth)) revert NotAllowedToSendEther();
        if (msg.sender == _curvePool) {
            _weth.deposit{value: address(this).balance}();
        }
    }

    function curvePool() public view returns (address) {
        return _getCurveBaseStorage()._curvePool;
    }

    function curveLp() public view returns (IERC20) {
        return _getCurveBaseStorage()._curveLp;
    }

    function curveGauge() public view returns (ILiquidityGaugeV2) {
        return _getCurveBaseStorage()._curveGauge;
    }

    function curvePoolZap() public view returns (address) {
        return _getCurveBaseStorage()._curvePoolZap;
    }

    function depositAndStake() public view returns (address) {
        return _getCurveBaseStorage()._depositAndStake;
    }

    function getUnderlyingTokens() public view returns (address[] memory) {
        return _getCurveBaseStorage()._underlyingTokens;
    }

    function isMetaPool() internal view returns (bool) {
        return _getCurveBaseStorage()._isMetaPool;
    }

    /// @dev Check whether given token is reserved or not. Reserved tokens are not allowed to sweep.
    function isReservedToken(address token_) public view override returns (bool) {
        return token_ == address(curveLp()) || token_ == address(collateralToken());
    }

    // Gets LP value not staked in gauge
    function lpBalanceHere() public view returns (uint256 _lpHere) {
        _lpHere = curveLp().balanceOf(address(this));
    }

    function lpBalanceHereAndStaked() public view returns (uint256 _lpHereAndStaked) {
        _lpHereAndStaked = curveLp().balanceOf(address(this)) + lpBalanceStaked();
    }

    function lpBalanceStaked() public view virtual returns (uint256 _lpStaked) {
        _lpStaked = curveGauge().balanceOf(address(this));
    }

    function masterOracle() public view returns (IMasterOracle) {
        return _getCurveBaseStorage()._masterOracle;
    }

    function withdrawOneCoin() public view returns (bool) {
        return _getCurveBaseStorage()._withdrawOneCoin;
    }

    function receiptToken() public view override returns (address) {
        return address(curveLp());
    }

    function slippage() public view returns (uint256) {
        return _getCurveBaseStorage()._slippage;
    }

    /// @notice Returns collateral balance + collateral deposited to curve
    function tvl() external view override returns (uint256) {
        return collateralToken().balanceOf(address(this)) + _quoteLpToCoin(lpBalanceHereAndStaked());
    }

    function _approveToken(uint256 amount_) internal virtual override {
        super._approveToken(amount_);

        CurveBaseStorage memory s = _getCurveBaseStorage();
        if (s._curvePoolZap != address(0)) {
            // It is needed for withdrawal
            s._curveLp.forceApprove(s._curvePoolZap, amount_);
        }

        address _swapper = address(swapper());
        address[] memory _underlyingTokens = s._underlyingTokens;
        uint256 _nCoins = _underlyingTokens.length;
        for (uint256 i; i < _nCoins; i++) {
            address _underlyingToken = _underlyingTokens[i];
            IERC20(_underlyingToken).forceApprove(_swapper, amount_);
            IERC20(_underlyingToken).forceApprove(s._depositAndStake, amount_);
        }

        // Gauge needs to be approved for stake via depositAndStake contract. Some Gauge doesn't support this method
        try ILiquidityGaugeV2(s._curveGauge).set_approve_deposit(s._depositAndStake, true) {} catch {}
    }

    function _claimRewards() internal override {
        ILiquidityGaugeV2 _curveGauge = curveGauge();
        if (block.chainid == 1) {
            CRV_MINTER.mint(address(_curveGauge));
        } else if (GAUGE_FACTORY.is_valid_gauge(address(_curveGauge))) {
            // On side chain gauge factory can mint CRV reward but only for valid gauge.
            GAUGE_FACTORY.mint(address(_curveGauge));
        }
        // solhint-disable-next-line no-empty-blocks
        try _curveGauge.claim_rewards() {} catch {
            // This call may fail in some scenarios
            // e.g. 3Crv gauge doesn't have such function
        }
    }

    // @dev Convex strategy will have to override this to unstake from gauge and stake in Booster
    function _deposit() internal virtual {
        _depositAndStakeToCurve();
    }

    function _depositAndStakeToCurve() internal {
        CurveBaseStorage memory s = _getCurveBaseStorage();
        uint256[] memory _depositAmounts;
        uint256 _minMintAmount;
        uint256 _ethValue;
        {
            bool _isAmountZero;
            (_depositAmounts, _minMintAmount, _ethValue, _isAmountZero) = _getDepositData(s);
            if (_isAmountZero) return;
        }

        IDepositAndStake(s._depositAndStake).deposit_and_stake{value: _ethValue}(
            s._depositContract,
            address(s._curveLp),
            address(s._curveGauge),
            s._underlyingTokens.length,
            s._underlyingTokens,
            _depositAmounts,
            _minMintAmount,
            s._useUnderlying,
            s._useDynamicArray,
            s._curvePoolForDeposit
        );
    }

    function _getCurvePoolInfo(
        IMetaRegistry registry_,
        address curvePool_,
        address _weth
    )
        internal
        view
        returns (address[] memory _underlyingTokens, uint256 _collateralIdx, bool _isMetaPool, bool _isLendingTokenPool)
    {
        /// Note: collateralToken() is defined in parent contract and must be initialized before reading it.
        address _collateralToken = address(collateralToken());
        // This is the actual number of underlying tokens in Curve pool
        uint256 _nCoins = registry_.get_n_underlying_coins(curvePool_);
        // We will track underlyingTokens array. It has length equal to _nCoins.
        _underlyingTokens = new address[](_nCoins);
        // get_underlying_coins always returns array of 8 length
        address[8] memory _underlyingCoins = registry_.get_underlying_coins(curvePool_);
        _collateralIdx = type(uint256).max;
        for (uint256 i; i < _nCoins; i++) {
            _underlyingTokens[i] = _underlyingCoins[i];
            if (_underlyingCoins[i] == _collateralToken || (_underlyingCoins[i] == ETH && _collateralToken == _weth)) {
                _collateralIdx = i;
            }
        }
        if (_collateralIdx > _nCoins) revert InvalidCollateral();

        _isMetaPool = registry_.is_meta(curvePool_);
        // we know that collateral is in _underlyingCoins but if it is not in get_coins then it is lendingTokenPool
        // A lendingToken pool is the one which hold lending(aToken, cToken) token as Curve collateral token.
        if (!_isMetaPool && _underlyingCoins[_collateralIdx] != registry_.get_coins(curvePool_)[_collateralIdx]) {
            _isLendingTokenPool = true;
        }
    }

    function _getDepositData(CurveBaseStorage memory s) private returns (uint256[] memory, uint256, uint256, bool) {
        uint256[] memory _depositAmounts = new uint256[](8);
        uint256 _minMintAmount;
        uint256 _ethValue;
        bool _isAmountZero = true; // Assume deposit amount is zero

        // Iterate through all underlying tokens.
        // Check balance of underlyingToken to determine depositAmount
        // Get quote of underlying token to collateral to calculate minimum out
        uint256 _nCoins = s._underlyingTokens.length;
        for (uint256 i; i < _nCoins; i++) {
            address _underlyingToken = s._underlyingTokens[i];
            // ETH can be found at max once in underlyingTokens.
            if (_underlyingToken == ETH) {
                if (_ethValue != 0) revert OnlyOneEthAllowedInUnderlying();
                IWETH _weth = s._weth;
                _depositAmounts[i] = _weth.balanceOf(address(this));
                _weth.withdraw(_depositAmounts[i]);
                _ethValue = _depositAmounts[i];
                _minMintAmount += _getAmountOutMin(address(_weth), address(s._curveLp), _depositAmounts[i]);
            } else {
                _depositAmounts[i] = IERC20(_underlyingToken).balanceOf(address(this));
                _minMintAmount += _getAmountOutMin(_underlyingToken, address(s._curveLp), _depositAmounts[i]);
            }
            // If deposit amount for any underlyingToken is non zero then set the zero flag to false
            if (_depositAmounts[i] > 0) _isAmountZero = false;
        }
        return (_depositAmounts, _minMintAmount, _ethValue, _isAmountZero);
    }

    function _getAmountOutMin(address tokenIn_, address tokenOut_, uint256 amountIn_) private view returns (uint256) {
        if (tokenIn_ == tokenOut_) {
            return amountIn_;
        } else {
            return (masterOracle().quote(tokenIn_, tokenOut_, amountIn_) * (MAX_BPS - slippage())) / MAX_BPS;
        }
    }

    function _quoteLpToCoin(uint256 amountIn_) private view returns (uint256 _amountOut) {
        if (amountIn_ == 0) {
            return 0;
        }

        IMetaRegistry _metaRegistry = IMetaRegistry(ADDRESS_PROVIDER.get_address(META_REGISTRY_ADDRESS_ID));
        address _collateralToken = address(collateralToken());

        address _curvePool = curvePool();
        // Notice we are reading number of pool coins and not underlying. In case Meta pools it will be 2.
        uint256 _nCoins = _metaRegistry.get_n_coins(_curvePool);
        address[8] memory _coins = _metaRegistry.get_coins(_curvePool);
        // Get balance of pool tokens,
        // in case of Meta pools
        //  - index 0 will be token in pool
        //  - index 1 will be lp token of base pool
        uint256[8] memory _coinBalances = _metaRegistry.get_balances(_curvePool);
        // CurveLp totalSupply
        uint256 _totalSupply = curveLp().totalSupply();
        // amountIn_ is basically amount of LP.
        uint256 _lpIn = amountIn_;

        if (isMetaPool()) {
            // If we have a Meta pool then
            //  - There will be 2 tokens in the pool. Index 0 is a Meta token, index 1 is lp of base pool.
            // We can use coinBalances[0] to calculate amountOut as we know token at index 0 is of the token of pool.
            uint256 _coinOut = (_lpIn * _coinBalances[0]) / _totalSupply;
            _amountOut += _getAmountOutMin(_coins[0], _collateralToken, _coinOut);

            // Now use coinBalances[1] to calculate amountOut.
            // For MetaPool This is the amount of LP of base pool.It becomes amountIn_ aka _lpIn for further calculation.
            // calculate new lpIn using coinBalance and totalSupply of Meta pool
            _lpIn = (_lpIn * _coinBalances[1]) / _totalSupply;

            // Given Meta pool, coin at index 1 is the LP of base Curve pool
            address _baseLp = _coins[1];
            // Use base lp to get address of base curve pool
            address _basePool = _metaRegistry.get_pool_from_lp_token(_baseLp);

            // For MetaPool, we will have to update TotalSupply as the totalSupply of base LP and not the Meta LP.
            // Same is true for nCoins, coins and coinBalances. We will update all of these will values corresponds to base pool.
            // update totalSupply and coinBalance from base pool
            _totalSupply = IERC20(_baseLp).totalSupply();
            _nCoins = _metaRegistry.get_n_coins(_basePool);
            _coins = _metaRegistry.get_coins(_basePool);
            _coinBalances = _metaRegistry.get_balances(_basePool);

            // Outside this if block, logic will process coins of base pool.
        }

        // iterate over number of tokens
        for (uint256 i; i < _nCoins; i++) {
            // use lpIn, coinBalances and totalSupply to calculate coinOut
            uint256 _coinOut = (_lpIn * _coinBalances[i]) / _totalSupply;
            // get quote of coin to collateral
            _amountOut += _getAmountOutMin(_coins[i], _collateralToken, _coinOut);
        }
    }

    function _rebalance() internal override returns (uint256 _profit, uint256 _loss, uint256 _payback) {
        IVesperPool _pool = IVesperPool(pool());
        uint256 _excessDebt = _pool.excessDebt(address(this));
        uint256 _totalDebt = _pool.totalDebtOf(address(this));

        IERC20 _collateralToken = collateralToken();
        uint256 _lpHere = lpBalanceHere();
        uint256 _totalLp = _lpHere + lpBalanceStaked();
        uint256 _collateralInCurve = _quoteLpToCoin(_totalLp);
        uint256 _collateralHere = _collateralToken.balanceOf(address(this));
        uint256 _totalCollateral = _collateralHere + _collateralInCurve;

        if (_totalCollateral > _totalDebt) {
            _profit = _totalCollateral - _totalDebt;
        } else {
            _loss = _totalDebt - _totalCollateral;
        }

        uint256 _profitAndExcessDebt = _profit + _excessDebt;
        if (_profitAndExcessDebt > _collateralHere) {
            uint256 _totalAmountToWithdraw = _profitAndExcessDebt - _collateralHere;
            uint256 _lpToBurn = Math.min((_totalAmountToWithdraw * _totalLp) / _collateralInCurve, _totalLp);
            if (_lpToBurn > 0) {
                _withdrawHere(_lpHere, _lpToBurn);
                _collateralHere = _collateralToken.balanceOf(address(this));
            }
        }

        // Make sure _collateralHere >= _payback + profit. set actual payback first and then profit
        _payback = Math.min(_collateralHere, _excessDebt);
        _profit = _collateralHere > _payback ? Math.min((_collateralHere - _payback), _profit) : 0;

        _pool.reportEarning(_profit, _loss, _payback);
        _deposit();
    }

    function _unstakeLp(uint256 amount_) internal virtual {
        if (amount_ > 0) {
            curveGauge().withdraw(amount_);
        }
    }

    /// @dev It is okay to set 0 as _amountOut as there is another check in place in calling function.
    function _withdrawFromCurve(uint256 nCoins_, uint256 lpToBurn_) internal virtual {
        CurveBaseStorage memory s = _getCurveBaseStorage();
        address _curvePool = s._curvePool;
        address _curvePoolZap = s._curvePoolZap;
        if (s._withdrawOneCoin) {
            _withdrawOneCoin(s, lpToBurn_);
            return;
        }
        if (s._useDynamicArray) {
            uint256[] memory _amountOut = new uint256[](nCoins_);
            IWithdraw(_curvePool).remove_liquidity(lpToBurn_, _amountOut);
        } else if (nCoins_ == 2) {
            uint256[2] memory _amountOut;
            if (_curvePoolZap != address(0)) {
                IWithdraw(_curvePoolZap).remove_liquidity(lpToBurn_, _amountOut);
            } else {
                IWithdraw(_curvePool).remove_liquidity(lpToBurn_, _amountOut);
            }
        } else if (nCoins_ == 3) {
            uint256[3] memory _amountOut;
            if (_curvePoolZap != address(0)) {
                IWithdraw(_curvePoolZap).remove_liquidity(_curvePool, lpToBurn_, _amountOut);
            } else {
                IWithdraw(_curvePool).remove_liquidity(lpToBurn_, _amountOut);
            }
        } else if (nCoins_ == 4) {
            uint256[4] memory _amountOut;
            if (_curvePoolZap != address(0)) {
                IWithdraw(_curvePoolZap).remove_liquidity(_curvePool, lpToBurn_, _amountOut);
            } else {
                IWithdraw(_curvePool).remove_liquidity(lpToBurn_, _amountOut);
            }
        }
    }

    function _withdrawHere(uint256 coinAmountOut_) internal override {
        uint256 _lpHere = lpBalanceHere();
        uint256 _totalLp = _lpHere + lpBalanceStaked();
        uint256 _lpToBurn = Math.min((coinAmountOut_ * _totalLp) / _quoteLpToCoin(_totalLp), _totalLp);
        if (_lpToBurn == 0) return;

        _withdrawHere(_lpHere, _lpToBurn);
    }

    function _withdrawHere(uint256 lpHere_, uint256 lpToBurn_) internal {
        if (lpToBurn_ > lpHere_) {
            _unstakeLp(lpToBurn_ - lpHere_);
        }

        address _collateralToken = address(collateralToken());
        uint256 _collateralBefore = IERC20(_collateralToken).balanceOf(address(this));
        // We can check amountOut_ against collateral received but it will serve as extra security measure
        uint256 _minAmountOut = _getAmountOutMin(address(curveLp()), _collateralToken, lpToBurn_);

        address[] memory _underlyingTokens = getUnderlyingTokens();
        uint256 _nCoins = _underlyingTokens.length;
        _withdrawFromCurve(_nCoins, lpToBurn_);

        for (uint256 i; i < _nCoins; i++) {
            address _underlyingToken = _underlyingTokens[i];
            if (_underlyingToken == _collateralToken) {
                continue;
            }
            uint256 _underlyingBalance = IERC20(_underlyingToken).balanceOf(address(this));
            if (_underlyingBalance > 0) {
                _swapExactInput(_underlyingToken, _collateralToken, _underlyingBalance);
            }
        }
        if (IERC20(_collateralToken).balanceOf(address(this)) - _collateralBefore < _minAmountOut)
            revert SlippageTooHigh();
    }

    function _withdrawOneCoin(CurveBaseStorage memory s, uint256 lpToBurn_) internal {
        // Withdraw is protected by collateral balance check at the end, so it if fine to use 1 as min out.
        uint256 _minOut = 1;
        int128 _i = int128(int256(s._collateralIdx));
        if (s._curvePoolZap != address(0)) {
            IWithdraw(s._curvePoolZap).remove_liquidity_one_coin(s._curvePool, lpToBurn_, _i, _minOut);
        } else if (s._useUnderlying) {
            IWithdraw(s._curvePool).remove_liquidity_one_coin(lpToBurn_, _i, _minOut, true);
        } else {
            IWithdraw(s._curvePool).remove_liquidity_one_coin(lpToBurn_, _i, _minOut);
        }
    }

    /************************************************************************************************
     *                          Governor/admin/keeper function                                      *
     ***********************************************************************************************/
    function toggleWithdrawOneCoinFlag() external onlyKeeper {
        CurveBaseStorage storage $ = _getCurveBaseStorage();
        bool _newValue = !$._withdrawOneCoin;
        emit WithdrawOneCoinToggled(_newValue);
        $._withdrawOneCoin = _newValue;
    }

    function updateSlippage(uint256 newSlippage_) external onlyGovernor {
        if (newSlippage_ >= MAX_BPS) revert InvalidSlippage();

        CurveBaseStorage storage $ = _getCurveBaseStorage();
        emit SlippageUpdated($._slippage, newSlippage_);
        $._slippage = newSlippage_;
    }

    function updateMasterOracle(IMasterOracle newMasterOracle_) external onlyGovernor {
        if (address(newMasterOracle_) == address(0)) revert AddressIsNull();

        CurveBaseStorage storage $ = _getCurveBaseStorage();
        emit MasterOracleUpdated($._masterOracle, newMasterOracle_);
        $._masterOracle = newMasterOracle_;
    }
}
